SELECT OWNERNAME,OWNERCERTNUM,OWNERNAME_PEIOU,OWNERCERTNUM_PEIOU,PEOPLE_STATUS,OPTYPENUM,RECYEAR,RECNUM,RecNumGather FROM ( SELECT OWNERNAME,OWNERCERTNUM,OWNERNAME_PEIOU,OWNERCERTNUM_PEIOU,PEOPLE_STATUS,OPTYPENUM,RECYEAR,RECNUM,RecNumGather, ROWNUM RN FROM (SELECT a.*,b.recnumgather, b.oppartnum FROM RWRECOWNER a,rwrecinfo b where a.optypenum = b.optypenum and a.recyear=b.recyear and a.recnum = b.recnum
and b.CURRECLOCKUSERNUM=#{userId}
and (a.people_status=3 or a.people_status=2)
and a.PEOPLE_STATUS =#{status}
and instr(a.OWNERNAME||a.OWNERCERTNUM||a.OWNERNAME_PEIOU||a.OWNERCERTNUM_PEIOU||b.RECNUMGATHER,#{condition})>0
) A WHERE #{iEnd}>= ROWNUM ) WHERE RN >= #{iStart}

SELECT count(*)
FROM (SELECT a.*,b.recnumgather,  b.oppartnum FROM RWRECOWNER a,rwrecinfo b where
a.optypenum = b.optypenum and a.recyear=b.recyear and a.recnum = b.recnum


        and b.CURRECLOCKUSERNUM=#{userId}


        and (a.people_status=3 or a.people_status=2)


        and  a.PEOPLE_STATUS =#{status}


        and
        instr(a.OWNERNAME||a.OWNERCERTNUM||a.OWNERNAME_PEIOU||a.OWNERCERTNUM_PEIOU||b.RECNUMGATHER,#{condition})>0


)
