mapper.select:
SELECT BUILDIDTEMP as "projectId",BUILDID as "buildId", SITBUILDNUM as "buildNum", FLOORCOUNT as "floorCount", UNITCOUNT as "unitCount" FROM TBBWDIAGRAMBUILD WHERE BUILDIDTEMP > 0 AND BUILDIDTEMP IS NOT NULL
AND BUILDIDTEMP = #{projectId}
AND SITBUILDNUM LIKE '%${condition}%'

mapper.update:
update TBBWDIAGRAMBUILD SET
sitbuildnum = #{projectName},
sitnumgather = #{sitnumgather},
districtcode = #{region}
where BUILDID = #{projectId}
